    void bufferedImage() throws IOException {
        File file = new File("C:\\Users\\PC\\OneDrive\\Pictures\\puzzle.jpg");
        BufferedImage originalImage = ImageIO.read(file);
         int rows=3,cols=3;
        // Tính kích thước của mỗi mảnh ảnh con
        int pieceWidth = originalImage.getWidth() / cols;
        int pieceHeight = originalImage.getHeight() / rows;
       
        // Mảng lưu trữ các mảnh ảnh con
        

        int count = 0;
        for (int y = 0; y < rows; y++) {
            for (int x = 0; x < cols; x++) {
                imageP[count] = new BufferedImage(pieceWidth, pieceHeight, originalImage.getType());
                java.awt.Graphics2D g = imageP[count++].createGraphics();
                g.drawImage(
                    originalImage,
                    0,
                    0,
                    pieceWidth,
                    pieceHeight,
                    pieceWidth * x,
                    pieceHeight * y,
                    pieceWidth * x + pieceWidth,
                    pieceHeight * y + pieceHeight,
                    null
                );
                g.dispose();
            }
        }
    }
    
    
    
