    void checkButton(JButton b1,JButton b2){
        String suffle=b2.getText();
        if(suffle==""){
            b2.setText(b1.getText());
            b1.setText("");
        }
    }
    //=======================================================================================
    public void shuffle(){
    int i = 1, j, row;
    int[] bnum = new int[9];
    boolean flag;

    do {
        Random rnd = new Random();
        row = rnd.nextInt(8) + 1;
        flag = false;

        for (j = 1; j < i; j++) {
            if (bnum[j] == row) {
                flag = true;
                break;
            }
        }

        if (!flag) {
            bnum[i] = row;
            i++;
        }
    } while (i <= 8);

    jButton1.setText(Integer.toString(bnum[1]));
    jButton2.setText(Integer.toString(bnum[2]));
    jButton3.setText(Integer.toString(bnum[3]));
    jButton4.setText(Integer.toString(bnum[4]));
    jButton5.setText(Integer.toString(bnum[5]));
    jButton6.setText(Integer.toString(bnum[6]));
    jButton7.setText(Integer.toString(bnum[7]));
    jButton8.setText(Integer.toString(bnum[8]));
    jButton9.setText("");   
    }
    //=======================================================================================
    
    public void Solution(){
        String b1= jButton1.getText();
        String b2= jButton2.getText();
        String b3= jButton3.getText();
        String b4= jButton4.getText();
        String b5= jButton5.getText();
        String b6= jButton6.getText();
        String b7= jButton7.getText();
        String b8= jButton8.getText();
         
        if(b1 == "1" && b2 == "2" && b3=="3"
                 && b4=="4" && b5 =="5" && b6 =="6"
                    && b7=="7" && b8=="8"){
             JOptionPane.showMessageDialog(this,"You Win","Puzzle Game",JOptionPane.INFORMATION_MESSAGE);
        }
        counter=counter+1;
        jLabelNumOfClicks.setText(Integer.toString(counter));
    }  
    
